SET NAMES UTF8;
DROP DATABASE IF EXISTS hebao;
CREATE DATABASE hebao CHARSET=UTF8;
USE hebao;

/*1、职位表*/
CREATE TABLE hb_job(
    jid INT PRIMARY KEY AUTO_INCREMENT,
    jname varchar(64),
    fenlei varchar(64),
    bumen varchar(64),
    position varchar(64),
    type int,
    zhize varchar(2048),
    detail varchar(2048),
    yaoqiu varchar(64),
    detail1 varchar(2048),
    email varchar(64)
);
INSERT INTO hb_job VALUES
('1','系统架构师','技术类','互联网产品部','深圳总部','1','岗位职责：','1、负责系统架构整体设计，技术架构选型，并主导功能模块设计、应用数据库设计、对外接口设计；<br>2、指导、承担系统核心功能的研发工作；负责核心技术问题的攻关，系统优化，协助解决项目开发过程中的技术难题；<br>3、积极了解业界发展，关注互联网相关新技术及趋势。','任职要求：','1、全日制本科及以上学历；<br>2、五年及以上互联网领域经验，有大型互联网公司经验者优先；<br>3、至少两年以上的架构设计工作经验，对互联网业务以及技术有深刻的理解；<br>4、精通网站性能优化、http协议、分布式、多线程等高性能架构相关技术；<br>5、精通面向对象的分析和设计技术，包括设计模式、UML建模等；<br>6、精通高负载Web、APP应用的关键技术，如负载均衡、集群、容灾备份、纵横向扩展；<br>7、熟悉现行主流NoSQL数据库、熟悉它们在不同场景下的优劣、保证高可用；<br>8、具有良好的沟通能力和平台级产品开发经验；<br>9、具备对未来互联网业务以及技术的敏感性，具备较强的技术规划能力，具备良好的敬业精神和团队合作精神。','如有意向，可将简历投递至 lannian@hebaodai.com'),
